//Take an integer N as input
//print odd integer from 1 to N using loop


package Loops;

public class Prog_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int N = 20;
		int i = 1;
		
		while (i<=N) {
		System.out.println(i);
			i= i+2;
			
		}
	}

}
