/*Take an integer N as input 
 * Print perfect squares tilL N  
 * Perfect Square : an integer whose square root is integer
 */


package Loops;
 

public class Prog_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int N= 20;
			int i = 1;
			while(i*i<=N) {
				System.out.println(i);
				i = i+1;
			}
	
	}

}
