package if_else_Ladder;

public class Prog_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 60;
		int y = 60;
		
		if (x>y) { 
			System.out.println("x is greater");
		}else if(x<y) {
			System.out.print("y is greater");
		}else{
			System.out.println("Both are equal");
		}
		
	}

}
